import React from 'react'
import { createOrder } from '../api/MoneyAPI';
import "../style/CreateOrder.scss";


class CreateOrder extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      amount: '',
      bankCode: '',
      language: 'vn', // Default language
    };
  }

  handleAmountChange = (e) => {
    this.setState({ amount: e.target.value });
  };

  handleBankCodeChange = (e) => {
    this.setState({ bankCode: e.target.value });
  };

  handleLanguageChange = (e) => {
    this.setState({ language: e.target.value });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
  };

  waitForRedirect = async () => {
    const info = {};
    info['amount'] = this.state.amount;
    info['bankCode'] = this.state.bankCode;
    info['language'] = this.state.language;

    //NOTE: the code bellow really shouldn't be here 
    info['userid'] = localStorage.getItem("userid");
    info['hotelid'] = localStorage.getItem("hoteldetailID");
    info['roomid'] = 1;
    info['price'] = 115000;
    info['daterange'] = localStorage.getItem('date').startDate + " " + localStorage.getItem('date').endDate;

    // hotelid: info.hotelid , 
    // userid: info.userid, 
    // roomid: info.roomid, 
    // price: info.price, 
    // daterange: info.daterange, 


    const result = await createOrder(info);
    console.log(result)
    window.location.href = result.data.redirectUrl;
    return null;
  }

  render() {
    const { amount, bankCode, language } = this.state;

    return (
      <div className='create-order-container'>
        <div className='create-order-content'>
          <div className='head-text'>Thông tin giao dịch</div>
          <div className='main-content'>
            <div className='content-logo'>
              <img src='data:image/png;base64,' />
            </div>

            <div className='detail-content'>
              {/* <hr /> */}
              <div className='first-line'>
                <div className='left-content'>
                  Số tiền
                </div>
                <div className='right-content'>
                  <input
                    className="form-control"
                    id="amount"
                    name="amount"
                    placeholder="Nhập số tiền"
                    value={amount}
                    onChange={this.handleAmountChange}
                  />
                </div>
              </div>
              {/* <hr /> */}
              <div className='second-line'>
                <div className='left-content'>
                  Phương thức thanh toán:
                </div>
                <div className='right-content'>
                  <select
                    name="bankCode"
                    value={bankCode}
                    onChange={this.handleBankCodeChange}
                  >
                    <option value="">Cổng thanh toán VNPAYQR</option>
                    <option value="VNPAYQR">Thanh toán qua ứng dụng hỗ trợ VNPAYQR</option>
                  </select>
                </div>
              </div>
              {/* <hr /> */}
              <div className='third-line'>
                <div className='left-content'>
                  Ngôn ngữ
                </div>
                <div className='right-content'>
                  <label className="radio-inline">
                    <input
                      type="radio"
                      name="language"
                      value="vn"
                      checked={language === 'vn'}
                      onChange={this.handleLanguageChange}
                    />
                    Tiếng việt
                  </label>
                  <label className="radio-inline">
                    <input
                      type="radio"
                      name="language"
                      value="en"
                      checked={language === 'en'}
                      onChange={this.handleLanguageChange}
                    />
                    Tiếng anh
                  </label>
                </div>
              </div>
              {/* <hr /> */}
            </div>
          </div>
          <div className='return-btn'>
            <button className="btn " type="submit" onClick={this.waitForRedirect}>Thanh toán</button>
          </div>
        </div>
      </div>
    );
  }
}

export default CreateOrder; 
